$(function(){$("#contact form").submit(function(){$("#contact form").console.log("contact initiated!");var e=$(this);var t=e.serialize();$.ajax({type:"POST",url:"contact.php",data:t,success:function(e){console.log(e);$("#contact .result .alert").remove();e=JSON.parse(e);if(e.status=="OK"){$("#contact .result").append('<div class="alert alert-success">Your message has been sent. Thank you!</div');$("#contact form").slideUp("slow")}else if(e.text){$.each(e.text,function(e,t){$("#contact .result").append('<div class="alert alert-error">'+t+"</div")})}else{$("#contact .result").append('<div class="alert alert-error">Error</div')}}});return false})})